// === GENERADOR DE URLs REMAX ALQUILER ===
// Misma zona Equipetrol, misma API, mismas páginas
// La API Remax devuelve VENTA y ALQUILER mezclados
// El filtrado se hace en 'Filtrar Solo Alquileres'

const TOTAL_PAGES = 8;
const BASE_URL = 'https://remax.bo/api/search/departamento/santa-cruz-de-la-sierra/equipetrolnoroeste';

const urls = [];

for (let page = 1; page <= TOTAL_PAGES; page++) {
  urls.push({
    json: {
      page_url: `${BASE_URL}?page=${page}`,
      page_number: page,
      fuente: 'remax'
    }
  });
}

return urls;

// === EXTRAER PROPIEDADES REMAX ===
// v1.0.0 Alquiler: Extrae TODAS, filtrado posterior
const response = $input.first().json;
const propiedades = response.data || [];

return propiedades.map(prop => ({
  json: {
    url: `https://remax.bo/propiedad/${prop.slug}`,
    fuente: 'remax',
    codigo_propiedad: prop.MLSID,
    tipo_operacion: prop.transaction_type?.name?.toLowerCase() || null,
    latitud: parseFloat(prop.location?.latitude) || null,
    longitud: parseFloat(prop.location?.longitude) || null,
    precio_bob: prop.price?.amount || null,
    precio_usd: prop.price?.price_in_dollars || null,
    moneda_original: prop.price?.currency_id === 1 ? 'BOB' : 'USD',
    area_total_m2: prop.listing_information?.construction_area_m || null,
    dormitorios: prop.listing_information?.number_bedrooms || null,
    banos: prop.listing_information?.number_bathrooms || null,
    estacionamientos: prop.listing_information?.number_parking || null,
    tipo_propiedad_original: prop.listing_information?.subtype_property?.name || null,
    fecha_publicacion: prop.date_of_listing || null,
    datos_json_discovery: prop,
    metodo_discovery: 'api_rest'
  }
}));

// === FILTRAR SOLO ALQUILERES ===
// La API Remax devuelve venta + alquiler mezclados
// Filtramos por transaction_type que contenga 'alquiler' o 'arriendo'
// Excluimos anticrético (modelo financiero distinto)

const snapshot = $input.first().json.propiedades;

const alquileres = snapshot.filter(prop => {
  const tipo = (prop.tipo_operacion || '').toLowerCase();
  // Aceptar: alquiler, arriendo, renta
  // Rechazar: venta, anticretico, anticrético
  return tipo.includes('alquiler') || tipo.includes('arriendo') || tipo.includes('renta');
});

const excluidos = snapshot.length - alquileres.length;

console.log(`Filtro alquiler: ${alquileres.length} alquileres de ${snapshot.length} total (${excluidos} excluidos)`);

return [{
  json: {
    propiedades: alquileres,
    stats_filtro: {
      total_api: snapshot.length,
      alquileres: alquileres.length,
      excluidos: excluidos
    }
  }
}];

// === PREPARAR COMPARACIÓN ALQUILERES ===
const snapshot = $('Filtrar Solo Alquileres').first().json.propiedades;
const propiedadesBD = $('Obtener Alquileres Activos BD').all().map(item => item.json);

const snapshotDedup = new Map();
for (const prop of snapshot) {
  snapshotDedup.set(prop.url, prop);
}

const urlsSnapshot = new Set(snapshotDedup.keys());
const urlsBD = new Map(propiedadesBD.map(p => [p.url, { id: p.id, status: p.status }]));

const nuevas = [];
const existentes = [];
const ausentes = [];

for (const [url, prop] of snapshotDedup) {
  if (!urlsBD.has(url)) {
    nuevas.push(prop);
  } else {
    existentes.push({
      ...prop,
      _id_bd: urlsBD.get(url).id,
      _status_bd: urlsBD.get(url).status
    });
  }
}

for (const [url, info] of urlsBD) {
  if (!urlsSnapshot.has(url)) {
    if (!['inactivo_pending', 'inactivo_confirmed'].includes(info.status)) {
      ausentes.push({ id: info.id, url: url, status_actual: info.status });
    }
  }
}

return [{
  json: {
    nuevas,
    existentes,
    ausentes,
    stats: {
      total_snapshot: snapshotDedup.size,
      total_bd: urlsBD.size,
      nuevas: nuevas.length,
      existentes: existentes.length,
      ausentes: ausentes.length
    }
  }
}];

// === LOG STATS ALQUILER ===
const stats = $input.first().json.stats;
const filtro = $('Filtrar Solo Alquileres').first().json.stats_filtro;

console.log(`
=== DISCOVERY REMAX ALQUILER ===
API total: ${filtro.total_api}
Alquileres filtrados: ${filtro.alquileres}
Excluidos (venta/antic): ${filtro.excluidos}
---
BD activas: ${stats.total_bd}
Nuevas: ${stats.nuevas}
Existentes: ${stats.existentes}
Ausentes: ${stats.ausentes}
================================
`);

return $input.all();

// === PREPARAR PARA registrar_discovery_alquiler() ===
const data = $input.first().json;
const nuevas = data.nuevas || [];
const existentes = data.existentes || [];
const todas = [...nuevas, ...existentes];

if (todas.length === 0) {
  return [{ json: { skip: true, message: 'No hay alquileres para procesar' } }];
}

return todas.map(prop => ({
  json: {
    p_url: prop.url,
    p_fuente: prop.fuente,
    p_codigo_propiedad: prop.codigo_propiedad,
    p_precio_mensual_bob: prop.precio_bob,
    p_precio_mensual_usd: prop.precio_usd,
    p_moneda_original: prop.moneda_original || 'BOB',
    p_area_total_m2: prop.area_total_m2,
    p_dormitorios: prop.dormitorios,
    p_banos: prop.banos,
    p_estacionamientos: prop.estacionamientos,
    p_tipo_propiedad_original: prop.tipo_propiedad_original,
    p_latitud: prop.latitud,
    p_longitud: prop.longitud,
    p_fecha_publicacion: prop.fecha_publicacion,
    p_metodo_discovery: prop.metodo_discovery,
    p_zona: null,
    p_datos_json_discovery: JSON.stringify(prop.datos_json_discovery)
  }
}));

// === PROCESAR AUSENTES ALQUILER ===
const ausentes = $input.first().json.ausentes;

if (ausentes.length === 0) {
  return [{ json: { skip: true, message: 'No hay alquileres ausentes' } }];
}

return ausentes.map(prop => ({
  json: {
    id: prop.id,
    url: prop.url,
    status_actual: prop.status_actual
  }
}));

// === RESUMEN FINAL ALQUILER ===
const stats = $('Log Stats').first().json.stats;
const filtro = $('Filtrar Solo Alquileres').first().json.stats_filtro;

const resumen = {
  timestamp: new Date().toISOString(),
  fuente: 'remax',
  workflow: 'Discovery Remax Alquiler',
  version: '1.0.0',
  resultados: {
    api_total: filtro.total_api,
    alquileres_filtrados: filtro.alquileres,
    excluidos_venta: filtro.excluidos,
    bd_activas: stats.total_bd,
    nuevas_insertadas: stats.nuevas,
    existentes_actualizadas: stats.existentes,
    ausentes_marcadas: stats.ausentes
  },
  status: 'completado'
};

console.log('=== DISCOVERY REMAX ALQUILER COMPLETADO ===');
console.log(JSON.stringify(resumen, null, 2));

return [{ json: resumen }];
