// === CONSTRUIR PROMPT PARA HAIKU v1.2.0 ===
// Híbrido: Remax usa data-page del HTML, C21 usa markdown
// [148] Agrega extracción de agente (nombre, teléfono, oficina)
// [158] Agrega extracción de fotos de listing.multimedias (Remax)
const prop = $('Loop Propiedades').first().json;
const scrape = $('Firecrawl Scrape').first().json;

let contenido = '';
let metodo = 'markdown';

// [148] Agente extraído directamente (Remax data-page)
let agente_directo = null;
// [158] Fotos extraídas directamente (Remax data-page)
let fotos_extraidas = null;

if (prop.fuente === 'remax') {
  // === REMAX: parsear data-page del HTML ===
  const html = scrape?.data?.rawHtml || scrape?.rawHtml || '';
  let titulo = '';
  let descripcion = '';

  if (html && html.length > 500) {
    try {
      const match = html.match(/data-page=\"([^\"]*?)\"/i);
      if (match && match[1]) {
        const jsonString = match[1]
          .replace(/&quot;/g, '"')
          .replace(/&amp;/g, '&')
          .replace(/&lt;/g, '<')
          .replace(/&gt;/g, '>');
        const data = JSON.parse(jsonString);
        const listing = data.props?.listing || data.props?.property || null;
        if (listing) {
          titulo = listing.title || '';
          descripcion = listing.description_website || listing.description || '';
          metodo = 'data-page';
        }
        // [148] Extraer agente del data-page (Remax tiene estructura agent)
        const agent = listing?.agent || data.props?.agent || null;
        if (agent) {
          agente_directo = {
            nombre: agent.user?.name_to_show || agent.name || null,
            telefono: agent.user?.phone_number || agent.user?.mobile_phone || agent.user?.phone || null,
            oficina: agent.office?.name || null
          };
        }
        // [158] Extraer fotos del data-page (Remax tiene listing.multimedias)
        const multimedias = listing?.multimedias || listing?.photos || listing?.images || [];
        if (Array.isArray(multimedias) && multimedias.length > 0) {
          const urls = multimedias
            .map(m => m.large_url || m.url || m.link || m.extra_large_url || null)
            .filter(u => u && typeof u === 'string' && u.startsWith('http'));
          const unicas = [...new Set(urls)];
          if (unicas.length > 0) {
            fotos_extraidas = unicas;
          }
        }
      }
    } catch (e) {
      console.log('Error parseando data-page: ' + e.message);
    }
  }

  if (!descripcion || descripcion.length < 20) {
    // Fallback a markdown si data-page falla
    const md = scrape?.data?.markdown || scrape?.markdown || '';
    if (md && md.length >= 50) {
      contenido = md.substring(0, 3000);
      metodo = 'markdown_fallback';
    } else {
      return [{ json: { skip: true, error: 'Remax: no data-page ni markdown', prop_id: prop.prop_id } }];
    }
  } else {
    contenido = (titulo ? 'TÍTULO: ' + titulo + '\n\n' : '') + 'DESCRIPCIÓN:\n' + descripcion.substring(0, 3000);
  }
} else if (prop.fuente === 'bien_inmuebles') {
  // === BIEN INMUEBLES: extraer fotos del HTML + usar markdown para LLM ===
  const html = scrape?.data?.rawHtml || scrape?.rawHtml || '';
  if (html && html.length > 200) {
    // Extraer todas las fotos de uploads/catalogo/pics/
    const fotoMatches = html.match(/uploads\/catalogo\/pics\/[^"'\s)]+/g) || [];
    const fotosUnicas = [...new Set(fotoMatches)].map(f => 'https://www.bieninmuebles.com.bo/admin/' + f);
    if (fotosUnicas.length > 0) {
      // Poner la foto principal (nomb_img del discovery) primero
      const nomb_img = prop.nomb_img || '';
      if (nomb_img) {
        const mainUrl = 'https://www.bieninmuebles.com.bo/admin/uploads/catalogo/pics/' + nomb_img;
        fotosUnicas.sort((a, b) => (a === mainUrl ? -1 : b === mainUrl ? 1 : 0));
      }
      fotos_extraidas = fotosUnicas;
      console.log(`Bien Inmuebles: ${fotosUnicas.length} fotos extraídas`);
    }
  }
  const markdown = scrape?.data?.markdown || scrape?.markdown || '';
  if (!markdown || markdown.length < 50) {
    // Sin markdown pero con fotos, igual continuamos
    if (fotos_extraidas) {
      contenido = 'Sin descripción disponible';
      metodo = 'fotos_only';
    } else {
      return [{ json: { skip: true, error: 'Bien Inmuebles: sin contenido ni fotos', prop_id: prop.prop_id } }];
    }
  } else {
    contenido = markdown.substring(0, 3000);
  }
} else {
  // === C21: usar markdown como siempre ===
  const markdown = scrape?.data?.markdown || scrape?.markdown || '';
  if (!markdown || markdown.length < 50) {
    return [{ json: { skip: true, error: 'Firecrawl no devolvió contenido', prop_id: prop.prop_id } }];
  }
  contenido = markdown.substring(0, 3000);
}

const prompt = `Eres un extractor de datos inmobiliarios para Bolivia.
Extraes datos de páginas web de propiedades en ALQUILER.
NUNCA inventes datos. Si no aparece en el texto, usa null.

DATOS YA CONOCIDOS (del discovery):
- URL: ${prop.url}
- Fuente: ${prop.fuente}
- Precio discovery: ${prop.precio_mensual_bob ? 'Bs ' + prop.precio_mensual_bob : 'desconocido'}
- Área: ${prop.area_total_m2 ? prop.area_total_m2 + ' m²' : 'desconocida'}
- Dormitorios: ${prop.dormitorios || 'desconocido'}
- Baños: ${prop.banos || 'desconocido'}
- Zona: ${prop.zona || 'desconocida'}

TEXTO DE LA PÁGINA:
${contenido}

Devuelve SOLO este JSON (sin explicaciones):
{
  "precio_mensual_bob": number | null,
  "precio_mensual_usd": number | null,
  "expensas_bs": number | null,
  "deposito_meses": number | null,
  "contrato_minimo_meses": number | null,
  "area_total_m2": number | null,
  "dormitorios": number | null,
  "banos": number | null,
  "estacionamientos": number | null,
  "baulera": boolean | null,
  "piso": number | null,
  "amoblado": "si" | "no" | "semi" | null,
  "acepta_mascotas": boolean | null,
  "servicios_incluidos": string[] | null,
  "nombre_edificio": string | null,
  "descripcion_limpia": string | null,
  "amenities_confirmados": string[] | null,
  "equipamiento_detectado": string[] | null,
  "agente_nombre": string | null,
  "agente_telefono": string | null,
  "agente_oficina": string | null
}`;

return [{
  json: {
    prop_id: prop.prop_id,
    prop_url: prop.url,
    fuente: prop.fuente,
    metodo_extraccion: metodo,
    agente_directo: agente_directo,
    fotos_extraidas: fotos_extraidas,
    prompt: prompt,
    contenido_length: contenido.length
  }
}];

// === PARSEAR RESPUESTA LLM + VALIDAR v1.2.0 ===
// [148] Agrega merge de agente: LLM + data-page directo (Remax)
// [158] Agrega fotos extraídas del data-page (Remax)
const prop_id = $('Construir Prompt').first().json.prop_id;
const prop_url = $('Construir Prompt').first().json.prop_url;
const agente_directo = $('Construir Prompt').first().json.agente_directo;
const fotos_extraidas = $('Construir Prompt').first().json.fotos_extraidas;
const response = $input.first().json;

const text = response?.content?.[0]?.text || '';
const tokens = response?.usage?.input_tokens + (response?.usage?.output_tokens || 0);

// Extraer JSON del texto (puede tener ```json wrapper)
let jsonStr = text;
const jsonMatch = text.match(/```(?:json)?\s*([\s\S]*?)```/);
if (jsonMatch) {
  jsonStr = jsonMatch[1].trim();
} else {
  // Intentar encontrar el primer { ... }
  const braceMatch = text.match(/\{[\s\S]*\}/);
  if (braceMatch) jsonStr = braceMatch[0];
}

let datos_llm;
let errores = [];
let requiere_revision = false;

try {
  datos_llm = JSON.parse(jsonStr);
} catch (e) {
  errores.push('json_parse_error: ' + e.message);
  requiere_revision = true;
  datos_llm = {};
}

// === VALIDACIONES ===
if (datos_llm.precio_mensual_bob !== null && datos_llm.precio_mensual_bob !== undefined) {
  if (datos_llm.precio_mensual_bob <= 0 || datos_llm.precio_mensual_bob > 50000) {
    errores.push('precio_fuera_rango: ' + datos_llm.precio_mensual_bob);
    datos_llm.precio_mensual_bob = null;
  }
}

if (datos_llm.area_total_m2 !== null && datos_llm.area_total_m2 !== undefined) {
  if (datos_llm.area_total_m2 < 15 || datos_llm.area_total_m2 > 500) {
    errores.push('area_fuera_rango: ' + datos_llm.area_total_m2);
    datos_llm.area_total_m2 = null;
  }
}

if (datos_llm.dormitorios !== null && datos_llm.dormitorios !== undefined) {
  if (datos_llm.dormitorios < 0 || datos_llm.dormitorios > 6) {
    errores.push('dormitorios_fuera_rango: ' + datos_llm.dormitorios);
    datos_llm.dormitorios = null;
  }
}

if (datos_llm.banos !== null && datos_llm.banos !== undefined) {
  if (datos_llm.banos < 0 || datos_llm.banos > 6) {
    errores.push('banos_fuera_rango: ' + datos_llm.banos);
    datos_llm.banos = null;
  }
}

if (datos_llm.expensas_bs !== null && datos_llm.expensas_bs !== undefined) {
  if (datos_llm.expensas_bs < 0 || (datos_llm.precio_mensual_bob && datos_llm.expensas_bs >= datos_llm.precio_mensual_bob)) {
    errores.push('expensas_sospechosas: ' + datos_llm.expensas_bs);
    datos_llm.expensas_bs = null;
  }
}

if (errores.length > 2) {
  requiere_revision = true;
}

// [148] Merge agente: data-page directo (Remax) tiene prioridad, LLM como fallback
const agente_final = {
  nombre: (agente_directo?.nombre) || datos_llm.agente_nombre || null,
  telefono: (agente_directo?.telefono) || datos_llm.agente_telefono || null,
  oficina: (agente_directo?.oficina) || datos_llm.agente_oficina || null
};
datos_llm.agente_nombre = agente_final.nombre;
datos_llm.agente_telefono = agente_final.telefono;
datos_llm.agente_oficina = agente_final.oficina;

// [158] Inyectar fotos extraídas del data-page (Remax)
if (fotos_extraidas && Array.isArray(fotos_extraidas) && fotos_extraidas.length > 0) {
  datos_llm.fotos_urls = fotos_extraidas;
}

return [{
  json: {
    prop_id: prop_id,
    prop_url: prop_url,
    datos_llm: datos_llm,
    tokens_usados: tokens || 0,
    errores: errores,
    requiere_revision: requiere_revision
  }
}];

// === RESUMEN ENRICHMENT LLM ===
const lote = $('Preparar IDs').first().json;
const stats = $('Stats desde BD').first().json;

return [{ json: {
  timestamp: new Date().toISOString(),
  workflow: 'Enrichment LLM Alquiler',
  version: '1.0.0',
  modelo: 'claude-haiku-4.5',
  total_lote: lote.total_lote,
  total_enriquecidas: parseInt(stats.procesadas),
  tokens_total: parseInt(stats.tokens_total),
  costo_usd_haiku: parseFloat(stats.costo_usd_haiku),
  calidad: {
    con_edificio: parseInt(stats.con_edificio),
    con_amoblado: parseInt(stats.con_amoblado),
    con_amenities: parseInt(stats.con_amenities),
    con_descripcion: parseInt(stats.con_descripcion)
  },
  status: 'completado'
}}];

// === FALLBACK: Firecrawl falló ===
const prop_id = $('Loop Propiedades').first().json.prop_id;
console.log(`WARN: Firecrawl falló para prop ${prop_id}, saltando enrichment`);
return [{ json: { skip: true, prop_id: prop_id, error: 'firecrawl_failed' } }];

// === PREPARAR QUERY DE STATS ===
const lote = $('Query Alquileres Sin Enrichment').all();
const ids = lote.map(item => item.json.prop_id).filter(id => id);
return [{ json: { ids: ids, total_lote: ids.length } }];
