// Sin propiedades pendientes de merge
const timestamp = new Date().toISOString();

return [{
  json: {
    status: 'completed',
    message: 'No hay propiedades pendientes de merge (status=actualizado)',
    total_procesadas: 0,
    exitosas: 0,
    errores: 0,
    timestamp: timestamp
  }
}];

// ============================================================================
// NODO: Procesar Resultado Merge
// Versión: 1.0.0
// ============================================================================

const item = $input.first();
const propData = $('Loop Propiedades').first().json;

// Parsear resultado del merge
let resultado = item.json.resultado;
if (typeof resultado === 'string') {
  try {
    resultado = JSON.parse(resultado);
  } catch (e) {
    resultado = { success: false, error: 'Parse error: ' + e.message };
  }
}

return [{
  json: {
    // Datos de la propiedad
    id: propData.id,
    codigo_propiedad: propData.codigo_propiedad,
    url: propData.url,
    fuente: propData.fuente,

    // Resultado del merge
    success: resultado.success || false,
    status_anterior: resultado.status_anterior || null,
    status_nuevo: resultado.status_nuevo || null,
    tiene_discrepancias: resultado.tiene_discrepancias || false,
    error: resultado.error || null,

    // Metadata
    timestamp: new Date().toISOString()
  }
}];

// ============================================================================
// NODO: Generar Resumen Final
// Versión: 1.0.0
// ============================================================================

const results = $input.first().json.results || [];

// Contadores
let exitosas = 0;
let errores = 0;
let con_discrepancias = 0;

const erroresDetalle = [];
const discrepanciasDetalle = [];

for (const r of results) {
  if (r.success) {
    exitosas++;
    if (r.tiene_discrepancias) {
      con_discrepancias++;
      discrepanciasDetalle.push({
        id: r.id,
        codigo: r.codigo_propiedad,
        fuente: r.fuente
      });
    }
  } else {
    errores++;
    erroresDetalle.push({
      id: r.id,
      codigo: r.codigo_propiedad,
      error: r.error
    });
  }
}

const total = results.length;
const timestamp = new Date().toISOString();

return [{
  json: {
    status: 'completed',
    flujo: 'Flujo Merge - Nocturno v1.0.0',
    timestamp: timestamp,

    // Métricas principales
    total_procesadas: total,
    exitosas: exitosas,
    errores: errores,
    con_discrepancias: con_discrepancias,

    // Porcentajes
    tasa_exito: total > 0 ? Math.round((exitosas / total) * 100) : 0,

    // Detalles (para debugging)
    errores_detalle: erroresDetalle.slice(0, 10), // Max 10
    discrepancias_detalle: discrepanciasDetalle.slice(0, 10), // Max 10

    // Para Slack
    emoji_status: errores === 0 ? '✅' : '⚠️',
    resumen_slack: `Merge nocturno: ${exitosas}/${total} exitosas${errores > 0 ? `, ${errores} errores` : ''}${con_discrepancias > 0 ? `, ${con_discrepancias} con discrepancias` : ''}`
  }
}];

