// === GENERADOR DE CUADRANTES CENTURY21 ===
// Grid geográfico para Equipetrol
// Basado en RESEARCH_CENTURY21_GRID.md

const LAT_SUR = -17.775;
const LAT_NORTE = -17.750;
const LON_OESTE = -63.205;
const LON_ESTE = -63.185;
const STEP = 0.010;

// Generar cookie auto-emitida
const cookieId = 'sici_' + Math.random().toString(36).substring(2, 15);
const cookie = `PHPSESSID=${cookieId}`;

const cuadrantes = [];
let gridId = 1;

for (let lat = LAT_SUR; lat < LAT_NORTE; lat += STEP) {
  for (let lon = LON_OESTE; lon < LON_ESTE; lon += STEP) {
    const south = lat;
    const west = lon;
    const north = lat + STEP;
    const east = lon + STEP;

    // Formato Century21: coordenadas_north,east,south,west
    const coordString = `coordenadas_${north.toFixed(6)},${east.toFixed(6)},${south.toFixed(6)},${west.toFixed(6)}`;

    cuadrantes.push({
      json: {
        grid_id: gridId++,
        path_coordenadas: coordString,
        cookie: cookie,
        fuente: 'century21'
      }
    });
  }
}

return cuadrantes;

// === EXTRAER PROPIEDADES DEL RESPONSE ===
// Parsing defensivo: maneja 3 estructuras posibles
// Mapeo mínimo + snapshot RAW completo

const response = $input.first().json;

// Parsing defensivo según research
let propiedades = [];
if (Array.isArray(response)) {
  propiedades = response;
} else if (response.results) {
  propiedades = response.results;
} else if (response.datas && response.datas.results) {
  propiedades = response.datas.results;
}

if (!propiedades || propiedades.length === 0) {
  return []; // Cuadrante vacío
}

return propiedades.map(prop => ({
  json: {
    // === IDENTIFICACIÓN ===
    url: `https://c21.com.bo${prop.urlCorrectaPropiedad || '/propiedad/' + prop.id}`,
    fuente: 'century21',
    codigo_propiedad: String(prop.id),

    // === GPS ===
    latitud: parseFloat(prop.lat) || null,
    longitud: parseFloat(prop.lon) || null,

    // === DATOS OBSERVADOS (sin normalización) ===
    // Century21 devuelve más datos pero Discovery solo usa mínimos
    precio_amount: prop.precio || null,
    moneda_original: prop.moneda || null,
    area_total_m2: prop.superficie || null,
    dormitorios: prop.dormitorios || null,
    banos: prop.banos || null,
    tipo_propiedad_original: prop.tipo || null,
    fecha_publicacion: null, // No disponible en grid API

    // === SNAPSHOT RAW COMPLETO ===
    datos_json_discovery: prop,

    // === METADATA ===
    metodo_discovery: 'map_grid'
  }
}));

// === PREPARAR COMPARACIÓN ===
// Clasifica: nuevas, existentes, ausentes
// Deduplicación intra-snapshot por URL (último visto)

const snapshot = $('Aggregate Snapshot').first().json.propiedades || [];
const propiedadesBD = $('Obtener URLs Activas BD').all().map(item => item.json);

// === DEDUPLICACIÓN INTRA-SNAPSHOT ===
// Se conserva el ÚLTIMO visto (estado más reciente del mercado)
// Crítico para Century21 por overlapping de cuadrantes
const snapshotDedup = new Map();
for (const prop of snapshot) {
  snapshotDedup.set(prop.url, prop); // último sobrescribe anteriores
}

const urlsSnapshot = new Set(snapshotDedup.keys());
const urlsBD = new Map(propiedadesBD.map(p => [p.url, { id: p.id, status: p.status }]));

// === CLASIFICACIÓN ===
const nuevas = [];
const existentes = [];
const ausentes = [];

// Propiedades en snapshot
for (const [url, prop] of snapshotDedup) {
  if (!urlsBD.has(url)) {
    nuevas.push(prop);
  } else {
    existentes.push({
      ...prop,
      _id_bd: urlsBD.get(url).id,
      _status_bd: urlsBD.get(url).status
    });
  }
}

// Propiedades ausentes (en BD pero no en snapshot)
for (const [url, info] of urlsBD) {
  if (!urlsSnapshot.has(url)) {
    if (!['inactivo_pending', 'inactivo_confirmed'].includes(info.status)) {
      ausentes.push({
        id: info.id,
        url: url,
        status_actual: info.status
      });
    }
  }
}

return [{
  json: {
    nuevas: nuevas,
    existentes: existentes,
    ausentes: ausentes,
    stats: {
      total_snapshot: snapshotDedup.size,
      total_bd: urlsBD.size,
      nuevas: nuevas.length,
      existentes: existentes.length,
      ausentes: ausentes.length
    }
  }
}];

// === LOG STATS ===
const stats = $input.first().json.stats;

console.log(`
=== COMPARACIÓN DISCOVERY CENTURY21 ===
Snapshot (deduplicado): ${stats.total_snapshot}
BD activas: ${stats.total_bd}
---
Nuevas: ${stats.nuevas}
Existentes: ${stats.existentes}
Ausentes: ${stats.ausentes}
=======================================
`);

return $input.all();

// === UNIFICAR PROPIEDADES PARA DISCOVERY ===
// Combina nuevas + existentes en un solo array
// registrar_discovery() decide internamente si es INSERT o UPDATE

const data = $input.first().json;
const nuevas = data.nuevas || [];
const existentes = data.existentes || [];

// Unificar: todas pasan por registrar_discovery()
const todas = [...nuevas, ...existentes];

if (todas.length === 0) {
  return [{ json: { skip: true, message: 'No hay propiedades para procesar' } }];
}

return todas.map(prop => ({
  json: {
    p_url: prop.url,
    p_fuente: prop.fuente,
    p_codigo_propiedad: prop.codigo_propiedad,
    p_tipo_operacion: null,
    p_tipo_propiedad_original: prop.tipo_propiedad_original,
    p_precio_usd_original: prop.precio_amount,
    p_moneda_original: prop.moneda_original,
    p_area_total_m2: prop.area_total_m2,
    p_dormitorios: prop.dormitorios,
    p_banos: prop.banos,
    p_latitud: prop.latitud,
    p_longitud: prop.longitud,
    p_fecha_publicacion: prop.fecha_publicacion,
    p_metodo_discovery: prop.metodo_discovery,
    p_datos_json_discovery: JSON.stringify(prop.datos_json_discovery)
  }
}));

// === PROCESAR AUSENTES ===
// Prepara UPDATE directo a inactivo_pending

const ausentes = $input.first().json.ausentes;

if (ausentes.length === 0) {
  return [{ json: { skip: true, message: 'No hay propiedades ausentes' } }];
}

return ausentes.map(prop => ({
  json: {
    id: prop.id,
    url: prop.url,
    status_actual: prop.status_actual
  }
}));

// === RESUMEN FINAL ===
const stats = $('Log Stats').first().json.stats;

const resumen = {
  timestamp: new Date().toISOString(),
  fuente: 'century21',
  workflow: 'Flujo A - Discovery',
  version: '1.0.0',
  resultados: {
    snapshot_total: stats.total_snapshot,
    bd_activas: stats.total_bd,
    nuevas_insertadas: stats.nuevas,
    existentes_actualizadas: stats.existentes,
    ausentes_marcadas: stats.ausentes
  },
  status: 'completado'
};

console.log('=== FLUJO A DISCOVERY CENTURY21 COMPLETADO ===');
console.log(JSON.stringify(resumen, null, 2));

return [{ json: resumen }];
